import React, { PureComponent } from 'react';
import Pic from '../Pictures/Pic.js'
import styles from './textBlock.module.css'

export default class PicComponent extends PureComponent {
    render() {
        const style = this.props.reverse ? styles.container+" "+styles.reverse : styles.container
        const lOrR = this.props.reverse ? styles.right : styles.left
        return (
            <div className={style + " " + this.props.style}>
                {this.props.src ?
                    <Pic src={this.props.src}>{this.props.name}</Pic>
                    :
                    <div className={styles.addMargin}>
                        {this.props.component}
                    </div>
                }
                <div className={lOrR}>
                    <h2 >{this.props.header}</h2>
                    <p>
                        {this.props.children}
                    </p>
                </div>
            </div>
        );
    }
}
